  // const cameraRef = React.useRef() as React.MutableRefObject<HTMLInputElement>;
  // const cameraRef = useRef();
  const cameraRef = useRef<PerspectiveCameraType>(null);
  // const cameraRef = useRef<HTMLDivElement>(null);

  useFrame(() => {
    // You can dynamically update the camera position here
    // For example, cameraRef.current.position.z += 0.01;
  });

  return (
    <PerspectiveCamera
      makeDefault
      ref={cameraRef}
      fov={75}
      position={[0, 0, 5]}
    />
  );
};

const WebGLScene = () => {
  return (
    <Canvas>
      <CameraControls />
      {/* Add other 3D objects or components here */}
    </Canvas>
  );
};

export default WebGLScene;
